import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { db } from '../firebase';

export const LinkForm = (props) => {

    const initialStateValues = {
        url: '',
        name: '',
        description: ''
    }

    const [values, setValues] = useState(initialStateValues)

    const handleInputChange = e => {
        const { name, value } = e.target;
        setValues({...values, [name]: value})
    };

    const handleSubmit = e => {
        e.preventDefault();
        
        if (!validateURL(values.url)) {
            return toast("Invalid URL", {
                type: "warning",
                autoClose: 1000,
            })
        }
        props.addOrEditLink(values);
        setValues({...initialStateValues})
    };

    const getLinkById = async (id) => {
        const doc = await db.collection('links').doc(id).get();
        setValues({...doc.data()});
    }

    useEffect(() => {
        if (props.currentId === '') {
            setValues({...initialStateValues});
        } else {
            getLinkById(props.currentId);
        }
    },[props.currentId]);

    return (
        <form className="card card-body" onSubmit={handleSubmit}>
            <div className="form-group input-group">
                <div className="input-group-text bg-light">
                    <i className="material-icons">insert_link</i>
                </div>
                <input type="text" className="form-control" placeholder="https://someurl.com" name="url" id="" onChange={handleInputChange} value={values.url}/>
            </div>
            <div className="form-group input-group">
                <div className="input-group-text bg-light">
                    <i className="material-icons">create</i>
                </div>
                <input type="text" className="form-control" placeholder="Website name" name="name" id="" onChange={handleInputChange} value={values.name}/>
            </div>
            <div className="form-group input-group">
                <textarea name="description" placeholder="Website description" className="form-control" id="" rows="3" onChange={handleInputChange} value={values.description}></textarea>
            </div>
            <button type="submit" className="btn btn-primary btn-block">{props.currentId === '' ? 'Save' : 'Update'}</button>
        </form>
    );
}

export default LinkForm;
